#include <iostream>void	print_word_in_uppercase(char *charpointer){	std::string	str(charpointer);	for (std::string::iterator i = str.begin(); i < str.end(); i++)		std::cout << static_cast<char>(std::toupper(*i));}int main(int ac, char **av){	if (ac == 1)	{		std::cout << "* LOUD AND UNBEARABLE FEEDBACK NOISE *" << std::endl;		return (0);	}	for (int i = 1; i < ac; i++)		print_word_in_uppercase(av[i]);	std::cout << std::endl;	return (0);}